import { takeLatest, put, all, call } from 'redux-saga/effects';

import {
  googleSignInStart,
  emailSignInStart,
  checkUserSession,
} from '../actionTypes';

import {
  auth,
  googleProvider,
  createUserProfile,
  getCurrentUser,
} from '../../firebase/firebase.utils';

import { authFailedAction, authSuccessAction } from '../user/user.actions';

export function* getSnapshotUserRef(userAuth) {
  try {
    const userRef = yield call(createUserProfile, userAuth);
    const userSnapshot = yield userRef.get();
    yield put(
      authSuccessAction({
        id: userSnapshot.id,
        ...userSnapshot.data(),
      }),
    );
  } catch (e) {
    yield put(authFailedAction(e));
  }
}

export function* signInWithGoogle() {
  try {
    const { user } = yield auth.signInWithPopup(googleProvider);
    yield getSnapshotUserRef(user);
  } catch (e) {
    yield put(authFailedAction(e));
  }
}

export function* onGoogleSignInStart() {
  yield takeLatest(googleSignInStart, signInWithGoogle);
}

export function* signInWithEmail({ payload: { email, password } }) {
  try {
    const { user } = yield auth.signInWithEmailAndPassword(email, password);
    yield getSnapshotUserRef(user);
  } catch (e) {
    yield put(authFailedAction(e));
  }
}

export function* onEmailSignInStart() {
  yield takeLatest(emailSignInStart, signInWithEmail);
}


export function* isUserAuthenticated() {
  try {
    const userAuth = yield getCurrentUser();
    if (!userAuth) return;
    yield getSnapshotUserRef(userAuth);
  } catch (e) {
    yield put(authFailedAction(e));
  }
}

export function* onCheckUserSession() {
  yield takeLatest(checkUserSession, isUserAuthenticated);
}


export function* userSagas() {
  yield all([call(onGoogleSignInStart), call(onEmailSignInStart), call(onCheckUserSession)]);
}
