import { takeLatest, put, all, call } from 'redux-saga/effects';
import { googleSignInStart, googleSignInFailed } from '../actionTypes';
import {
  auth,
  googleProvider,
  createUserProfile,
} from '../../firebase/firebase.utils';

import {
  googleSignInSuccessAction,
  googleSignInFailedAction,
} from '../user/user.actions';
export function* signInWithGoogle() {
  try {
    const { user } = yield auth.signInWithPopup(googleProvider);
    const userRef = yield call(createUserProfile, user);
    const userSnapshot = yield userRef.get();
    yield put(
      googleSignInSuccessAction({
        id: userSnapshot.id,
        ...userSnapshot.data(),
      }),
    );
  } catch (e) {
    yield put(googleSignInFailedAction(e));
  }
}

export function* onGoogleSignInStart() {
  yield takeLatest(googleSignInStart, signInWithGoogle);
}

export function* userSagas() {
  yield all([call(onGoogleSignInStart)]);
}
