import { updatedObject } from '../util';
const INITIAL_STATE = {
  currentUser: null,
};

const userReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case 'SET_CURRENT_USER':
      return updatedObject(state, { currentUser: action.payload });

    default:
      return state;
  }
};
export default userReducer;
