import {toggleCart, addItem, removeItem} from "../actionTypes"

export const toggleCartHidden = ()=>({
  type: toggleCart
})

export const addItemToCart = (item)=>({
  type: addItem,
  payload: item
})
export const removeItemFromCart = (id)=>({
  type: removeItem,
  payload: id
})
