import { toggleCart , addItem} from '../actionTypes';
import { updatedObject } from '../util';
const INITIAL_STATE = {
  hidden: true,
  cartItems: []
};

const cartReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case toggleCart:
      return updatedObject(state, { hidden: !state.hidden });
    case addItem:
    return updatedObject(state,{ cartItems:[...state.cartItems, action.payload.item]})
    default:
      return state;
  }
};

export default cartReducer
