import {
  emailSignInFailed,
  emailSignInStart,
  emailSignInSuccess,
  googleSignInFailed,
  googleSignInSuccess,
  googleSignInStart,
} from '../actionTypes';

export const googleSignInStartAction = () => ({
  type: googleSignInStart,
});
export const googleSignInFailedAction = err => ({
  type: googleSignInFailed,
  payload: err,
});
export const googleSignInSuccessAction = user => ({
  type: googleSignInSuccess,
  payload: user,
});

export const emailSignInStartAction = credential => ({
  type: emailSignInStart,
  payload: credential,
});

export const emailSignInFailedAction = err => ({
  type: emailSignInFailed,
  payload: err,
});

export const emailSignInSuccessAction = user => ({
  type: emailSignInSuccess,
  payload: user,
});
