import {
  emailSignInStart,
  googleSignInStart,
  authFailed,
  authSuccess,
} from '../actionTypes';

export const googleSignInStartAction = () => ({
  type: googleSignInStart,
});
export const authFailedAction = err => ({
  type: authFailed,
  payload: err,
});
export const authSuccessAction = user => ({
  type: authSuccess,
  payload: user,
});

export const emailSignInStartAction = credential => ({
  type: emailSignInStart,
  payload: credential,
});
